import React from 'react';
import { Link, useParams } from 'react-router-dom';

const subcats = {
  'fruits-vegetables': [
    { id: 'all', title: 'All' },
    { id: 'fresh-fruits', title: 'Fresh Fruits' },
    { id: 'fresh-vegetables', title: 'Fresh Vegetables' },
  ],
  'dairy-eggs': [
    { id: 'all', title: 'All' },
    { id: 'milk', title: 'Milk & Dairy' },
    { id: 'eggs', title: 'Eggs' },
  ],
};

const sampleFreshFruits = [
  { id: 'apple', name: 'Apple', price: 120, image: '/images/apple.png' },
  { id: 'banana', name: 'Banana', price: 40, image: '/images/bananas.png' },
  { id: 'mango', name: 'Mango', price: 200, image: '/images/mango.png' },
  { id: 'papaya', name: 'Papaya', price: 50 },
  { id: 'grapes', name: 'Grapes', price: 150 },
  { id: 'pineapple', name: 'Pineapple', price: 90 },
  { id: 'guava', name: 'Guava', price: 70 },
  { id: 'pear', name: 'Pear', price: 160 },
  { id: 'watermelon', name: 'Watermelon', price: 30 },
];

const sampleFreshVegetables = [
  { id: 'tomato', name: 'Tomato', price: 30 },
  { id: 'potato', name: 'Potato', price: 25 },
  { id: 'onion', name: 'Onion', price: 28 },
  { id: 'cabbage', name: 'Cabbage', price: 22 },
  { id: 'carrot', name: 'Carrot', price: 60 },
  { id: 'spinach', name: 'Spinach', price: 40 },
  { id: 'capsicum', name: 'Capsicum', price: 90 },
  { id: 'brinjal', name: 'Brinjal', price: 55 },
  { id: 'bottle-gourd', name: 'Bottle Gourd', price: 35 },
  { id: 'okra', name: 'Okra', price: 70 },
];

export default function CategoryDetail(){
  const { slug, sub } = useParams();
  const items = subcats[slug] || [{id:'all', title:'All'}];
  const showSub = sub || null;

  return (
    <div style={{maxWidth:1100,margin:'24px auto',padding:'0 16px'}}>
      <h2 style={{marginBottom:8, textTransform:'capitalize'}}>{slug.replace('-', ' & ')}</h2>
      <div style={{display:'flex',gap:16}}>
        <div style={{width:320}}>
          <div style={{border:'1px solid #eef2f7',borderRadius:8,overflow:'hidden',background:'#fff'}}>
            <div style={{padding:12,borderBottom:'1px solid #f1f5f9'}}>
              <strong>Categories</strong>
            </div>
            <div style={{padding:12}}>
              {items.map(it => (
                <Link key={it.id} to={`/category/${slug}/${it.id}`} style={{display:'block',padding:'12px',background:it.id===showSub ? '#e6f7ec' : (it.id==='fresh-fruits' ? '#fff' : 'transparent'),borderRadius:6,marginBottom:8,color:'#0f172a',textDecoration:'none'}}>
                  {it.title}
                </Link>
              ))}
            </div>
          </div>
        </div>

        <div style={{flex:1}}>
          {(!showSub || showSub === 'all') ? (
            <div>
              <h3 style={{marginBottom:12}}>Fresh Fruits — Price per kg</h3>
              <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(240px,1fr))',gap:12,marginBottom:18}}>
                {sampleFreshFruits.map(p => (
                  <div key={p.id} style={{padding:12,borderRadius:8,background:'#fff',border:'1px solid #eef2f7',display:'flex',gap:12,alignItems:'center'}}>
                    {p.image ? (
                      <img src={p.image} alt={p.name} style={{width:64,height:64,objectFit:'contain'}} />
                    ) : (
                      <div style={{width:64,height:64}} />
                    )}
                    <div>
                      <div style={{fontWeight:700}}>{p.name}</div>
                      <div style={{color:'#4b5563',marginTop:6}}>₹{p.price} / kg</div>
                    </div>
                  </div>
                ))}
              </div>

              <h3 style={{marginBottom:12}}>Fresh Vegetables — Price per kg</h3>
              <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(240px,1fr))',gap:12}}>
                {sampleFreshVegetables.map(p => (
                  <div key={p.id} style={{padding:12,borderRadius:8,background:'#fff',border:'1px solid #eef2f7'}}>
                    <div style={{fontWeight:700}}>{p.name}</div>
                    <div style={{color:'#4b5563',marginTop:6}}>₹{p.price} / kg</div>
                  </div>
                ))}
              </div>
            </div>
          ) : showSub=== 'fresh-fruits' ? (
            <div>
              <h3 style={{marginBottom:12}}>Fresh Fruits — Price per kg</h3>
              <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(240px,1fr))',gap:12}}>
                {sampleFreshFruits.map(p => (
                  <div key={p.id} style={{padding:12,borderRadius:8,background:'#fff',border:'1px solid #eef2f7'}}>
                    <div style={{fontWeight:700}}>{p.name}</div>
                    <div style={{color:'#4b5563',marginTop:6}}>₹{p.price} / kg</div>
                  </div>
                ))}
              </div>
            </div>
          ) : showSub === 'fresh-vegetables' ? (
            <div>
              <h3 style={{marginBottom:12}}>Fresh Vegetables — Price per kg</h3>
              <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(240px,1fr))',gap:12}}>
                {sampleFreshVegetables.map(p => (
                  <div key={p.id} style={{padding:12,borderRadius:8,background:'#fff',border:'1px solid #eef2f7'}}>
                    <div style={{fontWeight:700}}>{p.name}</div>
                    <div style={{color:'#4b5563',marginTop:6}}>₹{p.price} / kg</div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div style={{padding:16,background:'#fff',border:'1px solid #eef2f7',borderRadius:8}}>
              <div style={{color:'#6b7280'}}>Select a subcategory to view items.</div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
