import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import { useCart } from '../context/CartContext';

export default function Home() {
  const [products, setProducts] = useState([]);
  const { add } = useCart();

  useEffect(() => {
    fetch('/api/products')
      .then((r) => r.json())
      .then(setProducts)
      .catch((e) => { console.error(e); setProducts([]); });
  }, []);

  return (
    <>
      <section className="hero">
        <div className="hero-inner">
          <div>
            <h1>Fresh groceries delivered to your door</h1>
            <p>Quality produce, staples, and household essentials â€” fast delivery and great prices.</p>
          </div>
          <div>
            <img src="/images/bananas.svg" alt="hero" style={{ width: 220, height: 140, objectFit: 'contain' }} />
          </div>
        </div>
      </section>

      <div className="container">
        <h2 className="page-title">Popular products</h2>
        <div className="grid">
          {products.map((p) => (
            <ProductCard key={p._id} product={p} onAdd={(prod) => add(prod, 1)} />
          ))}
        </div>
      </div>
    </>
  );
}
