import React from 'react';
import CategoryCard from './CategoryCard';

const categories = [
  {title:'Fruits & Vegetables', image:'/images/apple.svg'},
  {title:'Dairy & Eggs', image:'/images/milk.svg'},
  {title:'Bakery', image:'/images/bread.svg'},
  {title:'Staples', image:'/images/rice.svg'},
  {title:'Snacks', image:'/images/cereal.svg'},
  {title:'Beverages', image:'/images/tea.svg'},
  {title:'Cooking Oils', image:'/images/oil.svg'},
];

export default function CategoryStrip(){
  return (
    <div style={{background:'#f8fafc',padding:'18px 0'}}>
      <div style={{maxWidth:1200,margin:'0 auto',padding:'0 16px'}}>
        <div style={{display:'flex',gap:12,overflowX:'auto',paddingBottom:4}}>
          {categories.map((c)=> (
            <CategoryCard key={c.title} title={c.title} image={c.image} />
          ))}
        </div>
      </div>
    </div>
  );
}
