import React from 'react';
import { Link, useParams } from 'react-router-dom';
import ProductCard from '../components/ProductCard';
import { useCart } from '../context/CartContext';

const subcats = {
  'fruits-vegetables': [
    { id: 'all', title: 'All' },
    { id: 'fresh-fruits', title: 'Fresh Fruits' },
    { id: 'fresh-vegetables', title: 'Fresh Vegetables' },
  ],
  'dairy-eggs': [
    { id: 'all', title: 'All' },
    { id: 'milk', title: 'Milk & Dairy' },
    { id: 'eggs', title: 'Eggs' },
  ],
  'bakery': [
    { id: 'all', title: 'All' }
  ],
  'staples': [
    { id: 'all', title: 'All' }
  ],
  'cooking-oils': [
    { id: 'all', title: 'All' }
  ],
  'snacks': [
    { id: 'all', title: 'All' }
  ],
};

const sampleFreshFruits = [
  { id: 'apple', name: 'Apple', price: 120, image: '/images/apple.png' },
  { id: 'banana', name: 'Banana', price: 40, image: '/images/bananas.png' },
  { id: 'mango', name: 'Mango', price: 200, image: '/images/mango.png' },
  { id: 'papaya', name: 'Papaya', price: 50, image: '/images/papaya.png' },
  { id: 'grapes', name: 'Grapes', price: 150, image: '/images/grapes.png' },
  { id: 'pineapple', name: 'Pineapple', price: 90, image: '/images/pineapple.png' },
  { id: 'guava', name: 'Guava', price: 70, image: '/images/guava.png' },
  { id: 'pear', name: 'Pear', price: 160, image: '/images/pear.png' },
  { id: 'watermelon', name: 'Watermelon', price: 30, image: '/images/watermelon.png' },
];

const sampleFreshVegetables = [
  { id: 'tomato', name: 'Tomato', price: 30, image: '/images/tomato.svg' },
  { id: 'potato', name: 'Potato', price: 25 },
  { id: 'onion', name: 'Onion', price: 28 },
  { id: 'cabbage', name: 'Cabbage', price: 22 },
  { id: 'carrot', name: 'Carrot', price: 60 },
  { id: 'spinach', name: 'Spinach', price: 40 },
  { id: 'capsicum', name: 'Capsicum', price: 90 },
  { id: 'brinjal', name: 'Brinjal', price: 55 },
  { id: 'bottle-gourd', name: 'Bottle Gourd', price: 35 },
  { id: 'okra', name: 'Okra', price: 70 },
];

const sampleDairy = [
  { id: 'full-cream-milk', name: 'Full Cream Milk 1L', price: 70, image: '/images/milk.svg' },
  { id: 'toned-milk', name: 'Toned Milk 1L', price: 60, image: '/images/milk.svg' },
  { id: 'salted-butter', name: 'Salted Butter 200g', price: 150, image: '/images/butter.svg' },
  { id: 'mozzarella-cheese', name: 'Mozzarella Cheese 200g', price: 420, image: '/images/cheese.svg' },
  { id: 'cheddar-cheese', name: 'Cheddar Cheese 200g', price: 450, image: '/images/cheese.svg' },
  { id: 'fresh-cream', name: 'Fresh Cream 200ml', price: 90, image: '/images/cream.svg' },
  { id: 'plain-yogurt', name: 'Plain Yogurt 500g', price: 160, image: '/images/yogurt.svg' },
  { id: 'greek-yogurt', name: 'Greek Yogurt 400g', price: 280, image: '/images/yogurt.svg' },
  { id: 'ghee', name: 'Ghee 500g', price: 650, image: '/images/ghee.svg' },
  { id: 'milk-powder', name: 'Milk Powder 500g', price: 500, image: '/images/milk-powder.svg' },
  { id: 'eggs', name: 'Eggs (dozen)', price: 200, image: '/images/eggs.svg' },
];

const sampleBakery = [
  { id: 'white-bread', name: 'White Bread', price: 120, image: '/images/bread.svg' },
  { id: 'whole-wheat-bread', name: 'Whole Wheat Bread', price: 150, image: '/images/bread.svg' },
  { id: 'croissant', name: 'Croissant', price: 90, image: '/images/bread.svg' },
  { id: 'muffin', name: 'Muffin', price: 80, image: '/images/bread.svg' },
  { id: 'cookie', name: 'Cookie', price: 50, image: '/images/bread.svg' },
  { id: 'donut', name: 'Donut', price: 60, image: '/images/bread.svg' },
];

const sampleStaples = [
  { id: 'basmati-rice', name: 'Basmati Rice 1kg', price: 450, image: '/images/rice.svg' },
  { id: 'wheat-flour', name: 'Wheat Flour (Atta) 1kg', price: 180, image: '/images/cereal.svg' },
  { id: 'sunflower-oil', name: 'Sunflower Cooking Oil 1L', price: 390, image: '/images/oil.svg' },
  { id: 'turmeric', name: 'Turmeric Powder 100g', price: 90, image: '/images/turmeric.svg' },
  { id: 'toor-dal', name: 'Toor Dal 1kg', price: 320, image: '/images/dal.svg' },
  { id: 'iodized-salt', name: 'Iodized Salt 1kg', price: 70, image: '/images/salt.svg' },
];

const sampleCookingOils = [
  { id: 'sunflower-oil', name: 'Sunflower Oil 1L', price: 390, image: '/images/oil.svg' },
  { id: 'groundnut-oil', name: 'Groundnut Oil 1L', price: 420, image: '/images/oil.svg' },
  { id: 'mustard-oil', name: 'Mustard Oil 1L', price: 370, image: '/images/oil.svg' },
  { id: 'coconut-oil', name: 'Coconut Oil 1L', price: 550, image: '/images/oil.svg' },
  { id: 'olive-oil', name: 'Olive Oil 500ml', price: 800, image: '/images/oil.svg' },
  { id: 'rice-bran-oil', name: 'Rice Bran Oil 1L', price: 400, image: '/images/oil.svg' },
];

const sampleSnacks = [
  { id: 'potato-chips', name: 'Potato Chips', price: 50, image: '/images/chips.png' },
  { id: 'salted-peanuts', name: 'Salted Peanuts', price: 80, image: '/images/peanuts.png' },
  { id: 'masala-namkeen', name: 'Masala Namkeen (Mixture)', price: 90, image: '/images/namkeen.png' },
  { id: 'popcorn', name: 'Popcorn', price: 60, image: '/images/popcorn.png' },
  { id: 'chocolate-bar', name: 'Chocolate Bar', price: 120, image: '/images/chocolate.png' },
  { id: 'biscuits', name: 'Biscuits / Cookies', price: 70, image: '/images/biscuits.png' },
];

export default function CategoryDetail(){
  const { slug, sub } = useParams();
  const items = subcats[slug] || [{id:'all', title:'All'}];
  const showSub = sub || null;
  const { add } = useCart();

  const toProduct = (p) => ({
    _id: p._id || p.id || p.name,
    name: p.name,
    description: p.description || (p.price ? `₹${p.price} / kg` : ''),
    image: p.image || '/placeholder.png',
    price: typeof p.price === 'number' ? p.price : 0,
    countInStock: p.countInStock || 10,
  });

  const renderGridFrom = (arr) => (
    <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(240px,1fr))',gap:12}}>
      {arr.map(p => (
        <div key={p.id || p._id || p.name} style={{padding:6}}>
          <ProductCard product={toProduct(p)} onAdd={(prod) => add(prod,1)} />
        </div>
      ))}
    </div>
  );

  return (
    <div style={{maxWidth:1100,margin:'24px auto',padding:'0 16px'}}>
      <h2 style={{marginBottom:8, textTransform:'capitalize'}}>{slug.replace('-', ' & ')}</h2>
      <div style={{display:'flex',gap:16}}>
        <div style={{width:320}}>
          <div style={{border:'1px solid #eef2f7',borderRadius:8,overflow:'hidden',background:'#fff'}}>
            <div style={{padding:12,borderBottom:'1px solid #f1f5f9'}}>
              <strong>Categories</strong>
            </div>
            <div style={{padding:12}}>
              {items.map(it => (
                <Link key={it.id} to={`/category/${slug}/${it.id}`} style={{display:'block',padding:'12px',background:it.id===showSub ? '#e6f7ec' : (it.id==='fresh-fruits' ? '#fff' : 'transparent'),borderRadius:6,marginBottom:8,color:'#0f172a',textDecoration:'none'}}>
                  {it.title}
                </Link>
              ))}
            </div>
          </div>
        </div>

        <div style={{flex:1}}>
          {slug === 'fruits-vegetables' && ((!showSub || showSub === 'all')) ? (
            <div>
              <h3 style={{marginBottom:12}}>Fresh Fruits — Price per kg</h3>
              {renderGridFrom(sampleFreshFruits)}

              <h3 style={{marginBottom:12}}>Fresh Vegetables — Price per kg</h3>
              {renderGridFrom(sampleFreshVegetables)}
            </div>
          ) : slug === 'fruits-vegetables' && showSub=== 'fresh-fruits' ? (
            <div>
              <h3 style={{marginBottom:12}}>Fresh Fruits — Price per kg</h3>
              {renderGridFrom(sampleFreshFruits)}
            </div>
          ) : slug === 'fruits-vegetables' && showSub === 'fresh-vegetables' ? (
            <div>
              <h3 style={{marginBottom:12}}>Fresh Vegetables — Price per kg</h3>
              {renderGridFrom(sampleFreshVegetables)}
            </div>
          ) : slug === 'dairy-eggs' && ((!showSub || showSub === 'all')) ? (
            <div>
              <h3 style={{marginBottom:12}}>Dairy & Eggs</h3>
              {renderGridFrom(sampleDairy)}
            </div>
          ) : slug === 'dairy-eggs' && showSub === 'milk' ? (
            <div>
              <h3 style={{marginBottom:12}}>Milk Products</h3>
              <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(240px,1fr))',gap:12}}>
                {sampleDairy.filter(p => p.id.includes('milk')).map(p => (
                  <div key={p.id} style={{padding:12,borderRadius:8,background:'#fff',border:'1px solid #eef2f7'}}>
                    <div style={{fontWeight:700}}>{p.name}</div>
                    <div style={{color:'#4b5563',marginTop:6}}>₹{p.price}</div>
                  </div>
                ))}
              </div>
            </div>
          ) : slug === 'dairy-eggs' && showSub === 'eggs' ? (
            <div>
              <h3 style={{marginBottom:12}}>Eggs</h3>
              <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(240px,1fr))',gap:12}}>
                {sampleDairy.filter(p => p.id === 'eggs').map(p => (
                  <div key={p.id} style={{padding:12,borderRadius:8,background:'#fff',border:'1px solid #eef2f7'}}>
                    <div style={{fontWeight:700}}>{p.name}</div>
                    <div style={{color:'#4b5563',marginTop:6}}>₹{p.price}</div>
                  </div>
                ))}
              </div>
            </div>
          ) : slug === 'bakery' && ((!showSub || showSub === 'all')) ? (
            <div>
              <h3 style={{marginBottom:12}}>Bakery</h3>
              <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(240px,1fr))',gap:12}}>
                {renderGridFrom(sampleBakery)}
              </div>
            </div>
          ) : slug === 'staples' && ((!showSub || showSub === 'all')) ? (
            <div>
              <h3 style={{marginBottom:12}}>Staples</h3>
              <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(240px,1fr))',gap:12}}>
                {sampleStaples.map(p => (
                  <div key={p.id} style={{padding:12,borderRadius:8,background:'#fff',border:'1px solid #eef2f7',display:'flex',gap:12,alignItems:'center'}}>
                    {p.image ? (
                      <img src={p.image} alt={p.name} style={{width:64,height:64,objectFit:'contain'}} />
                    ) : (
                      <div style={{width:64,height:64}} />
                    )}
                    <div>
                      <div style={{fontWeight:700}}>{p.name}</div>
                      <div style={{color:'#4b5563',marginTop:6}}>₹{p.price}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : slug === 'cooking-oils' && ((!showSub || showSub === 'all')) ? (
            <div>
              <h3 style={{marginBottom:12}}>Cooking Oils</h3>
              <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(240px,1fr))',gap:12}}>
                {sampleCookingOils.map(p => (
                  <div key={p.id} style={{padding:12,borderRadius:8,background:'#fff',border:'1px solid #eef2f7',display:'flex',gap:12,alignItems:'center'}}>
                    {p.image ? (
                      <img src={p.image} alt={p.name} style={{width:64,height:64,objectFit:'contain'}} />
                    ) : (
                      <div style={{width:64,height:64}} />
                    )}
                    <div>
                      <div style={{fontWeight:700}}>{p.name}</div>
                      <div style={{color:'#4b5563',marginTop:6}}>₹{p.price}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : slug === 'snacks' && ((!showSub || showSub === 'all')) ? (
            <div>
              <h3 style={{marginBottom:12}}>Snacks</h3>
              <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(240px,1fr))',gap:12}}>
                {sampleSnacks.map(p => (
                  <div key={p.id} style={{padding:12,borderRadius:8,background:'#fff',border:'1px solid #eef2f7',display:'flex',gap:12,alignItems:'center'}}>
                    {p.image ? (
                      <img src={p.image} alt={p.name} style={{width:64,height:64,objectFit:'contain'}} />
                    ) : (
                      <div style={{width:64,height:64}} />
                    )}
                    <div>
                      <div style={{fontWeight:700}}>{p.name}</div>
                      <div style={{color:'#4b5563',marginTop:6}}>₹{p.price}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div style={{padding:16,background:'#fff',border:'1px solid #eef2f7',borderRadius:8}}>
              <div style={{color:'#6b7280'}}>Select a subcategory to view items.</div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
